Bash(node -e "
const configs = [''bsc'', ''ethereum'', ''polygon'', ''arbitrum'', ''base'', ''avalanche''];
const validHex = /^0x[a-fA-F0-9]{40}$/;

for \(const chain of configs\) {
    const config = await import\(''./src/config/chains/'' + chain + ''.js''\);
    const tokens = config.default.tokens;
    for \(const [symbol, token] of Object.entries\(tokens\)\) {
        if \(!validHex.test\(token.address\)\) {
            console.log\(chain + ''.'' + symbol + '': INVALID - '' + token.address\);
        }
    }
}
console.log\(''Done checking addresses''\);
")
